import { useState, useEffect } from 'react'

//récupère les données d'une API
export function useFetch2(url) {
    const [data, setData] = useState({})//données API
    const [isLoading, setLoading] = useState(true)//indique si les données sont en train de se charger
    const [error, setError] = useState(false)//erreurs API

    useEffect(() => {
        if (!url) return

        setLoading(true)//affiche le loader

        fetch(url)
            .then((response) => response.json()//convertit la réponse en données JSON
            .then(({ data }) => {//traite les données reçues
                setData(data)
            })
            .catch((error) => {//attrape les erreurs lors de l’appel de l’API s'il y en a
                console.log(error)
                setError(true)
            })
        )

        setTimeout(() => {setLoading(false)}, 500)//enlève le loader. setTimeout sert juste à confirmer l'affichage du loader en cas de délai de chargement des données
    }, [url])//déclenche l'appel de useEffect si l'url change

    return {isLoading, data, error}
}

export function useFetch(url) {
    const [data, setData] = useState({})//données API
    const [isLoading, setLoading] = useState(true)//indique si les données sont en train de se charger
    const [error, setError] = useState(false)//erreurs API

    useEffect(() => {
        if (!url) return

        setLoading(true)//affiche le loader

        //définition de la fonction fetchData
        async function fetchData() {
            try {
                const response = await fetch(url)
                const data = await response.json()
                setData(data)
            } catch (err) {
                console.log(err)
                setError(true)
            } finally {
                setLoading(false)
            }
        }

        //appel de la fonction fetchData
        fetchData()
    }, [url])//déclenche l'appel de useEffect si l'url change

    return { isLoading, data, error }
}


