import { useState, createContext  } from 'react'
export const ThemeContext = createContext()//contexte thème pour le site

/* provider pour le contexte théme */
export const ThemeProvider = ({ children }) => {
    const [theme, setTheme] = useState('light')//state theme
    const toggleTheme = () => {//remplace la valeur su state theme selon sa valeur actuelle
        setTheme(theme === 'light' ? 'dark' : 'light')
    }

    return (
        <ThemeContext.Provider value={{ theme, toggleTheme }}>
            {children}
        </ThemeContext.Provider>
    )
}
