import { Link } from 'react-router-dom'
import '../../styles/Results.css';
import { useContext } from 'react'
import { SurveyContext } from '../../utils/context/context'
import { useFetch } from '../../utils/hooks/hooks'

//formate les réponses pour les passer dans l'url
function formatFetchParams(answers) {
    const answerNumbers = Object.keys(answers)//numéros des réponses

    /* reduce ((boucle sur les n° de réponses):
     * previousParams = paramètres de l'url se mettant à jour à chq boucle. Initialisé à ''
     * answerNumber = le numéro de la réponse active
    */
    return answerNumbers.reduce((previousParams, answerNumber, index) => {
        const isFirstParam = index === 0
        const separator = isFirstParam ? '' : '&'

        return `${previousParams}${separator}a${answerNumber}=${answers[answerNumber]}`
    }, '')
}

function Results() {
    const { answers } = useContext(SurveyContext)
    const fetchParams = formatFetchParams(answers)

    const { data, isLoading, error } = useFetch(`http://localhost:8000/results?${fetchParams}`)
    const { resultsData } = data//rappel : le nom de la var doit être la même que le nom renvoyé par l'API

    return (
        <div className="row p-5 align-items-center bg-grey">
            <div className="text-center h2">
                <span>Les compétences dont vous avez besoin :</span>
                    {/* boucle et affiche les titres */}
                    {resultsData && resultsData.map(({ title }, index) => (
                        <div key={index} className="coloredText">{ title }{index === resultsData.length - 1 ? '' : ','}</div>
                    ))}
                <div><Link to="/" className="mt-5 px-5 btn rounded-pill bg-purple">Découvrez nos profils</Link></div>
            </div>

            {/* boucle et affiche le titre et la description de chaque résultats */}
            {resultsData && resultsData.map(({ title, description }, index) => (/* boucle sur le tableau cart et affiche les éléments */
                <div key={index} className="row justify-content-center">
                    <div className="col-12 col-sm-10 col-md-8 col-lg-6 mt-5 mx-5">
                        <span className="coloredText">{ title }</span>
                        <p className="pResults">{ description }.</p>
                    </div>
                </div>
            ))}
        </div>
    )
}

export default Results;
