import { Link } from 'react-router-dom'

function Survey() {
    const { questionNumber } = useParams()

    //parseInt : convertit en entier
    const questionNumberInt = parseInt(questionNumber)

    const previous = questionNumberInt - 1
    const next = questionNumberInt + 1

    //on utilise le caract√®re ` en jsx pour concat√©ner une cha√Æne et une variable
    return (
        <div>
            <h1>Questionnaire üßÆ</h1>
            <h2>Question {questionNumberInt}</h2>
            {/* liens */}
            {/*   affiche Pr√©c√©dent si on est pas √† la question 1 */ }
            {questionNumberInt !== 1 && <Link to={`/survey/${previous}`}>Pr√©c√©dent</Link>}
            {/*   affiche soit Suivant soit R√©sultats selon si on est √† la question 10 */}
            {questionNumberInt === 10 ? (
                <Link to="/results">R√©sultats</Link>
            ) : (
                <Link to={`/survey/${next}`}>Suivant</Link>
            )}
        </div>
    )
}

export default Survey;
