import { createContext,  useState} from 'react'

//contexte theme pour le site
export const ThemeContext = createContext('light')

/* provider pour le contexte theme */
export const ThemeProvider = ({ children }) => {
    const [theme, setTheme] = useState('light')//state theme light par défaut
    const toggleTheme = () => {//remplace la valeur du state theme selon sa valeur actuelle
        setTheme(theme === 'light' ? 'dark' : 'light')
    }

    return (
        <ThemeContext.Provider value={{ theme, toggleTheme }}>{/* le provider permet l'accès à theme et toggleTheme à ses enfants */}
            {children}
        </ThemeContext.Provider>
    )
}

/* provider pour le contexte survey */
export const SurveyProvider = ({ children }) => {
