import { useState, useEffect, useContext } from 'react'
import { ThemeContext } from '../context/context'

//récupère les données d'une API
export function useFetch(url) {
    const [data, setData] = useState({})
    const [isLoading, setLoading] = useState(false)//indique si les données sont en train de se charger
    const [error, setError] = useState(false)

    useEffect(() => {
        if (!url) return

        setLoading(true)//affiche le loader

        fetch(url)
            .then((response) => response.json())//convertit la réponse en données JSON
            .then((data) => {//traite les données reçues
                setData(data)

            })
            .catch((error) => {
                console.log(error)
                setError(true)
            })

        //enlève le loader. setTimeout sert juste à confirmer l'affichage du loader en cas de délai de chargement des données
        setTimeout(() => {setLoading(false)}, 500)
    }, [url])//déclenche l'appel de useEffect si l'url change

    return {isLoading, data, error}
}

//Exemple n°2 récupère les données d'une API 
export function useFetch2(url) {
    const [data, setData] = useState({})
    const [isLoading, setLoading] = useState(false)//indique si les données sont en train de se charger
    const [error, setError] = useState(false)

    useEffect(() => {
        if (!url) return

        setLoading(true)//affiche le loader

        //déclaration de la fonction fetchData
        async function fetchData() {
            try {
                const response = await fetch(url)
                const data = await response.json()
                setData(data)
            } catch (err) {
                console.log(err)
                setError(true)
            } finally {//traitement après l'appel
                setLoading(false)
            }
        }

        //appel de la fonction fetchData
        fetchData()
    }, [url])//déclenche l'appel de useEffect si l'url change

    return { isLoading, data, error }
}

//permet de récupérer le thème définit et sa fonction de modification
export function useTheme() {
    const { theme, toggleTheme } = useContext(ThemeContext)
    return { theme, toggleTheme }
}
