import { createContext,  useState} from 'react'

export const themes = {
    light: {
        foreground: '#000000',
        background: '#eeeeee'
    },
    dark: {
        foreground: '#ffffff',
        background: '#222222'
    },
};

//contexte theme pour le site
export const ThemeContext = createContext('light')

/* provider pour le contexte theme */
export const ThemeProvider = ({ children }) => {
    const [theme, setTheme] = useState('light')//state theme
    const toggleTheme = () => {//remplace la valeur su state theme selon sa valeur actuelle
        setTheme(theme === 'light' ? 'dark' : 'light')
    }

    return (
        <ThemeContext.Provider value={{ theme, toggleTheme }}>{/* le provider permet l'accès à theme et toggleTheme à ses enfants */}
            {children}
        </ThemeContext.Provider>
    )
}
