import { useParams, useNavigate } from 'react-router-dom'//useParams pour récupérer les params de l'url
import { Link } from 'react-router-dom'
import { useContext } from 'react'
import { SurveyContext } from '../../utils/context/context'
import { useFetch } from '../../utils/hooks/hooks'

function Survey() {
    //récupère le n° de la question actuelle
    const { questionNumber } = useParams()
    const questionNumberInt = parseInt(questionNumber)//parseInt : convertit en entier
    //numéro de question suivant et précédent
    const previous = questionNumberInt - 1
    const next = questionNumberInt + 1
    //récupère les données API, le statut du chargement et le statut de l'erreur
    //ne pas oublier de lancer 7150606-API-React-intermediaire en console pour accéder à http://localhost:8000
    const { data, isLoading, error } = useFetch(`http://localhost:8000/survey`)
    /* attention surveyData est indéfini tant que data n'est pas initialisé dans l'appel API donc on vérifie que surveyData est définie
    avant de s'en servir. Ex : surveyData && surveyData[questionNumberInt] */
    //de plus il faut nommer la var comme dans celle renvoyée par l'API !
    const { surveyData } = data 
    /* récupère la fonction saveAnswers via SurveyContext
    ces var sont accessibles car Survey est dans la balise SurveyProvider dans index.jsx */
    const { saveAnswers, answers } = useContext(SurveyContext)

    let navigate = useNavigate();




    //sauvegarde la réponse à la question et redirige sur la page des résultats si besoin
    function saveReply(event, answer) {
        saveAnswers({ [questionNumberInt]: answer })

        const buttons = document.querySelectorAll("button")
        const target = event.target

        for (const button of buttons) {
            if (target.innerHTML === button.innerHTML) {
                target.classList.add("selected")
            } else {
                button.classList.remove("selected")
            }
        };

        //Je laisse en exemple mais je ne m'en sert plus
        //s'il n'y a plus d'autres questions
//        if (!(surveyData[questionNumberInt + 1])) {
//            event.preventDefault();//stop le traitement après le click
//            navigate("/results", { replace: true });//redirige sur la page de résultats
//        }
    }








    if (error) {
        return (
            <div className="row">
                <span className="col col-sm-2 col-md-3 col-lg-4"></span>
                <span className="col-12 col-sm-8 col-md-6 col-lg-4 d-flex justify-content-center alert alert-danger" role="alert">
                    <strong>Erreur!</strong>&nbsp; Oups il y a eu un problème.
                </span>
                <span className="col col-sm-2 col-md-3 col-lg-4"></span>
            </div>
        )
    }


    React.useEffect(() => {


        const buttonTrue = document.getElementById('buttonTrue')
        const buttonFalse = document.getElementById('buttonTrue')

        buttonFalse.classList.remove("selected")
        buttonTrue.classList.remove("selected")


        const answersArray = Object.entries(answers);

        answersArray.forEach(([key, value]) => {
            console.log("key : " + key)
            console.log("questionNumberInt : " + questionNumberInt)
            console.log("value : " + value)


            if (key === questionNumberInt && value === true) {
                buttonTrue.classList.add("selected")
                buttonFalse.classList.remove("selected")
            }
            if (key === questionNumberInt && value === false) {
                buttonFalse.classList.add("selected")
                buttonTrue.classList.remove("selected")
            }
        });


    }, []);



    //rque : on utilise le caractère ` en jsx pour concaténer une chaîne et une variable
    return (
        <div className="text-center">
            {/* question */}
            <h1 className="underline-purple">Question {questionNumberInt}</h1>

            {isLoading ? (
                <div className="spinner-border" role="status"></div>
            ) : (
                <div className="mt-5">{surveyData && surveyData[questionNumberInt]}</div>
            )}

            {/* boutons Oui et Non */}
            <div className="row mt-5 justify-content-center">
                <button id="buttonTrue" className="col-2 p-3 mx-2 btn rounded-pill bg-grey button" onClick={(e) => saveReply(e, true)}>Oui</button>
                <div className="col-auto"></div>
                <button id="buttonFalse" className="col-2 p-3 mx-2 btn rounded-pill bg-grey button" onClick={(e) => saveReply(e, false)}>Non</button>
            </div>

            {/* liens */}
            <div className="mt-5">
                {questionNumberInt !== 1 && <Link className="mx-5 link" to={`/survey/${previous}`}>Précédente</Link>}
                {surveyData && surveyData[questionNumberInt + 1] && <Link className="mx-5 link" to={`/survey/${next}`}>Suivante</Link>}
                {surveyData && !(surveyData[questionNumberInt + 1]) && <Link className="mx-5 link" to="/results">Résultats</Link>}
            </div>
        </div>
    )
}

export default Survey;
