import Card from '../../components/Card/Card';
import { useState, useEffect } from 'react'
import DefaultPicture from '../../assets/profile.png'
import '../../styles/Freelances.css';

function Freelances() {
    const [freelancesData, setFreelancesData] = useState({})//freelances
    const [isDataLoading, setDataLoading] = useState(false)//loader

    //récuppère les questions par l'API dont l'adresse est http://localhost:8000
    useEffect(() => {
        setDataLoading(true)//affiche le loader

        fetch(`http://localhost:8000/freelances`)
            .then((response) => response.json()//convertit la réponse en données JSON
            .then(({ freelancesData }) => {//traite les données reçues
                setFreelancesData(freelancesData)
            })
            .catch((error) => console.log(error))//attrape les erreurs lors de l’appel de l’API s'il y en a
        )

        setDataLoading(false)//enlève le loader
     }, [])//[] : exécute un effet uniquement au premier rendu du composant

    //datas
    const freelanceProfiles = [
        {
            name: 'Jane Doe',
            jobTitle: 'UX Designer',
            picture: DefaultPicture,
        },
        {
            name: 'Alexandra Dupont',
            jobTitle: 'Developpeur frontend',
            picture: DefaultPicture,
        },
        {
            name: 'Jeanne Biche',
            jobTitle: 'Devops',
            picture: DefaultPicture,
        },
        {
            name: 'Lauren Ipsum',
            jobTitle: 'UX Designer',
            picture: DefaultPicture,
        }
    ]

    return (
        <div>
            <div className="text-center">
                <h1 className="mb-5">Trouvez votre prestataire</h1>
                <span className="text-secondary">Chez Shiny nous réunissons les meilleurs profils pour vous.</span>
            </div>
            <div id="cards" className="row d-flex justify-content-center mt-5">
                {freelancesData.map((freelance, index) => (
                    <Card
                        key={`${freelance.name}-${index}`}
                        label={freelance.jobTitle}
                        picture={freelance.picture}
                        title={freelance.name}
                    />
                ))}
            </div>
        </div>
    )
}

export default Freelances;
