import { useParams } from 'react-router-dom'//useParams pour récupérer les params de l'url
import { Link } from 'react-router-dom'
import '../../styles/Survey.css';

function Survey() {
    const { questionNumber } = useParams()

    //parseInt : convertit en entier
    const questionNumberInt = parseInt(questionNumber)

    const previous = questionNumberInt - 1
    const next = questionNumberInt + 1

    //on utilise le caractère ` en jsx pour concaténer une chaîne et une variable
    return (
        <div className="text-center">
            <h1 className="underline-blue">Question {questionNumberInt}</h1>

            <div className="mt-5">Votre application doit-elle impérativement apparaître en premier dans les résultats de recherche?</div>

            <div className="row mt-5 align-items-center">
                <div className="col-3"></div>
                <div className="col-3 btn rounded-pill bg-grey p-2 mx-3">Oui</div>

                <div className="col-3 btn rounded-pill bg-grey p-2 mx-3">Non</div>
                <div className="col-3"></div>
            </div>

            <div className="mt-5">
                {/* liens */}
                {/*   affiche Précédent si on est pas à la question 1 */ }
                {questionNumberInt !== 1 && <Link className="mx-5" to={`/survey/${previous}`}>Précédente</Link>}
                {/*   affiche soit Suivant soit Résultats selon si on est à la question 10 */}
                {questionNumberInt === 10 ? (
                    <Link to="/results" className="mx-5">Résultats</Link>
                ) : (
                    <Link className="mx-5" to={`/survey/${next}`}>Suivante</Link>
                )}
            </div>
        </div>
    )
}

export default Survey;
