import { createContext, useState} from 'react'

//CONTEXTE THEME
export const ThemeContext = createContext('light')

export const ThemeProvider = ({ children }) => {
    const [theme, setTheme] = useState('light')//state theme light par défaut
    const toggleTheme = () => {//remplace la valeur du state theme selon sa valeur actuelle
        setTheme(theme === 'light' ? 'dark' : 'light')
    }

    return (
        <ThemeContext.Provider value={{ theme, toggleTheme }}>{/* rend accessible : theme et toggleTheme */}
            {children}
        </ThemeContext.Provider>
    )
}

//CONTEXTE SURVEY
export const SurveyContext = createContext([])

export const SurveyProvider = ({ children }) => {
    const [answers, setAnswers] = useState([])
    const updateAnswers = (newAnswer) => {
        if (Object.entries(newAnswer).length === 0) {
            setAnswers([])//réinitialise les réponses du questionnaire
        } else {
            setAnswers({ ...answers, ...newAnswer })
        }
    }

    return (
        <SurveyContext.Provider value={{ answers, updateAnswers }}>{/* rend accessible : answers, updateAnswers */}
            {children}
        </SurveyContext.Provider>
    )
}
