import { createGlobalStyle } from 'styled-components'//pour créer un style global
import { useContext } from 'react'
import { ThemeContext } from '../utils/context/ThemeContext'

//on définit le style
const StyledDynamicStyle = createGlobalStyle`
    body {
        font-family: 'Trebuchet MS', Helvetica, sans-serif;

        background-color: ${({ isDarkMode }) =>
          isDarkMode ? '#2F2E41' : 'white'};
    }
`

function DynamicStyle() {
    /*
      récupère la var theme de ThemeContext
      cette var est accessible car DynamicStyle est dans la balise ThemeProvider dans index.jsx
    */
    const { theme } = useContext(ThemeContext)

    //CSS StyledDynamicStyle selon la prop isDarkMode dépendant de la var theme
    return <StyledDynamicStyle isDarkMode={theme === 'dark'} />
}

export default DynamicStyle
