import { Link } from 'react-router-dom'
import '../../styles/Results.css';
import { useContext } from 'react'
import { SurveyContext } from '../../utils/context/context'
import { useFetch } from '../../utils/hooks/hooks'

//formate les réponses pour les passer dans l'url
function formatFetchParams(answers) {
    const answerNumbers = Object.keys(answers)//numéros des réponses

    /* reduce ((boucle sur les n° de réponses):
     * previousParams = paramètres de l'url se mettant à jour à chq boucle. Initialisé à ''
     * answerNumber = le numéro de la réponse active
    */
    return answerNumbers.reduce((previousParams, answerNumber, index) => {
        const isFirstParam = index === 0
        const separator = isFirstParam ? '' : '&'

        return `${previousParams}${separator}a${answerNumber}=${answers[answerNumber]}`
    }, '')
}

function Results() {
    const { answers } = useContext(SurveyContext)
    const fetchParams = formatFetchParams(answers)

    const { data, isLoading, error } = useFetch(`http://localhost:8000/results?${fetchParams}`)
//    const { freelancersList } = data

    console.log(data.resultsData)

    return (

        <div className="row p-5 align-items-center bg-grey">

            {data.map(({ description, title }, index) => (/* boucle sur le tableau cart et affiche les éléments */
                <div key={index}>
                    <span className="coloredText">{ title }</span>
                </div>
            ))}




            <div className="text-center h2">
                <span>Les compétences dont vous avez besoin :</span>
                <div className="coloredText">UX Design, Frontend, Backend</div>
                <Link to="/" className="mt-5 px-5 btn rounded-pill bg-purple">Découvrez nos profils</Link>
            </div>

            <div className="row justify-content-center">
                <div className="col-12 col-sm-10 col-md-8 col-lg-6 mt-5 mx-5">
                    <span className="coloredText">UX Design</span>
                    <p className="pResults">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus viverra aliquam lorem rutrum aliquet. Suspendisse potenti. Sed tortor velit, consequat a auctor tempus, maximus non lacus.</p>

                    <span className="coloredText">Frontend</span>
                    <p className="pResults">Aliquam tincidunt eu metus et egestas. Quisque accumsan arcu vel risus auctor elementum. Curabitur at justo vel eros mattis gravida eu dapibus orci.</p>

                    <span className="coloredText">Backend</span>
                    <p className="pResults">Vestibulum quis libero ac augue cursus vestibulum. Quisque faucibus nisl risus, et convallis justo luctus vel.</p>
                </div>
            </div>
        </div>
    )
}

export default Results;
