import { useContext } from 'react'
import { ThemeProvider } from '../../utils/context/context'
import '../../styles/Footer.css';

function Footer() {
    /*
      récupère la fonction toggleTheme de Context
      cette var est accessible car Footer est dans la balise ThemeProvider dans index.jsx
    */
    const { toggleTheme } = useContext(ThemeProvider)

    return (
        <footer className="row mt-5 text-center">
            <span className="col-5"></span>
            <button className="col-2 btn rounded-pill bg-purple" onClick={() => toggleTheme()}>Changer de mode</button>
            <span className="col-5"></span>
        </footer>
    )
}

export default Footer;
