import Card from '../../components/Card/Card';
import { useState, useEffect } from 'react'
//import DefaultPicture from '../../assets/profile.png'
import '../../styles/Freelances.css'

function Freelances() {
    const [freelancesData, setFreelancesData] = useState([])//freelances. note : freelancersList renvoi une réponse []
    const [isDataLoading, setDataLoading] = useState(false)//loader
    const [error, setError] = useState(false)//error API


    //récupère les freelancers par l'API dont l'adresse est http://localhost:8000
    useEffect(() => {
        setDataLoading(true)//affiche le loader

        fetch(`http://localhost:8000/freelances`)
            .then((response) => response.json()//convertit la réponse en données JSON
            .then(({ freelancersList }) => {//traite les données reçues
                setFreelancesData(freelancersList)
            })
            .catch((error) => {//attrape les erreurs lors de l’appel de l’API s'il y en a
                console.log(error)
                setError(error)
            })
        )

        setDataLoading(false)
     }, [])//[] : exécute un effet uniquement au premier rendu du composant


    if (error) {
        return (
            <div className="row">
                <span className="col col-sm-2 col-md-3 col-lg-4"></span>
                <span className="col-12 col-sm-8 col-md-6 col-lg-4 d-flex justify-content-center alert alert-danger" role="alert">
                    <strong>Erreur!</strong>&nbsp; Oups il y a eu un problème.
                </span>
                <span className="col col-sm-2 col-md-3 col-lg-4"></span>
            </div>
        )
    }

    //datas statiques
//    const freelancesData = [
//        {
//            name: 'Jane Doe',
//            jobTitle: 'UX Designer',
//            picture: DefaultPicture,
//        },
//        {
//            name: 'Alexandra Dupont',
//            jobTitle: 'Developpeur frontend',
//            picture: DefaultPicture,
//        },
//        {
//            name: 'Jeanne Biche',
//            jobTitle: 'Devops',
//            picture: DefaultPicture,
//        },
//        {
//            name: 'Lauren Ipsum',
//            jobTitle: 'UX Designer',
//            picture: DefaultPicture,
//        }
//    ]

    return (
        <div>
            <div className="text-center">
                <h1 className="mb-5">Trouvez votre prestataire</h1>
                <span className="text-secondary">Chez Shiny nous réunissons les meilleurs profils pour vous.</span>
            </div>

            {isDataLoading ? (
                <div class="mt-5 d-flex justify-content-center">
                    <div class="spinner-border justify-content-center" role="status"></div>
                </div>
            ) : (
                <div id="cards" className="row d-flex justify-content-center mt-5">
                    {freelancesData.map((freelance) => (
                        <Card
                            key={`${freelance.name}-${freelance.id}`}
                            label={freelance.jobTitle}
                            picture={freelance.picture}
                            title={freelance.name}
                        />
                    ))}
                </div>
            )} 
        </div>
    )
}

export default Freelances;
