import { useParams } from 'react-router-dom'//useParams pour récupérer les params de l'url
import { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import '../../styles/Survey.css';

function Survey() {
    //récupère le n° de la question actuelle
    const { questionNumber } = useParams()
    const questionNumberInt = parseInt(questionNumber)//parseInt : convertit en entier
    //numéro de question suivant et précédent
    const previous = questionNumberInt - 1
    const next = questionNumberInt + 1
    //questions
    const [surveyData, setSurveyData] = useState({})
    //loader :: affiche un loader si la page met du temps à charger la question
    const [isDataLoading, setDataLoading] = useState(false)

    //récuppère les questions par l'API dont l'adresse est http://localhost:8000
    useEffect(() => {
       setDataLoading(true)
        fetch(`http://localhost:8000/survey`)
            .then((response) => response.json()//convertit la réponse en données JSON
            .then(({ surveyData }) => {//traite les données reçues
                setSurveyData(surveyData)
            })
            .catch((error) => console.log(error))//attrape les erreurs lors de l’appel de l’API s'il y en a
        )
        setDataLoading(false)
     }, [])//[] : exécute un effet uniquement au premier rendu du composant

    console.log(surveyData)

    //on utilise le caractère ` en jsx pour concaténer une chaîne et une variable
    return (
        <div className="text-center">
            <h1 className="underline-purple">Question {questionNumberInt}</h1>

            <div class="spinner-border" role="status">
                <span class="sr-only">Loading...</span>
            </div>
            <div className="mt-5">{surveyData[questionNumberInt]}</div>

            <div className="row mt-5 justify-content-center">
                <div className="col-2 p-3 mx-2 btn rounded-pill bg-grey btnSurvey">Oui</div>
                <div className="col-auto"></div>
                <div className="col-2 p-3 mx-2 btn rounded-pill bg-grey btnSurvey">Non</div>
            </div>

            {/* liens */}
            <div className="mt-5">
                {/* affiche Précédente si on est pas à la question 1 */ }
                {questionNumberInt !== 1 && <Link className="mx-5 link" to={`/survey/${previous}`}>Précédente</Link>}
                {/* affiche soit Suivante soit Résultats selon si la question +1 existe */}
                {surveyData[questionNumberInt + 1] ? (
                    <Link className="mx-5 link" to={`/survey/${next}`}>Suivante</Link>
                ) : (
                    <Link to="/results" className="mx-5 link">Résultats</Link>
                )}
            </div>
        </div>
    )
}

export default Survey;
